'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var isInteger = require( '@stdlib/assert/is-integer' );
var codePointAt = require( '@stdlib/string/code-point-at' );
var hasUTF16SurrogatePairAt = require( '@stdlib/assert/has-utf16-surrogate-pair-at' );
var shouldBreak = require( './should_break.js' );
var getGraphemeBreakProperty = require( './get_grapheme_break_property.js' );
var getEmojiProperty = require( './get_emoji_property.js' );


// MAIN //

/**
* Returns the previous extended grapheme cluster break in a string before a specified position.
*
* @param {string} str - input string
* @param {integer} [fromIndex=str.length-1] - position
* @throws {TypeError} first argument must be a string primitive
* @throws {TypeError} second argument must be an integer
* @returns {NonNegativeInteger} previous grapheme break position
*
* @example
* var out = prevGraphemeClusterBreak( 'last man standing', 4 );
* // returns 3
*
* @example
* var out = prevGraphemeClusterBreak( 'presidential election', 8 );
* // returns 7
*
* @example
* var out = prevGraphemeClusterBreak( 'अनुच्छेद', 2 );
* // returns 0
*
* @example
* var out = prevGraphemeClusterBreak( '🌷', 1 );
* // returns -1
*/
function prevGraphemeClusterBreak( str, fromIndex ) {
	var prevEmoji;
	var nextEmoji;
	var midEmoji;
	var prevCP;
	var nextCP;
	var prev;
	var next;
	var len;
	var mid;
	var ans;
	var idx;
	var i;

	if ( !isString( str ) ) {
		throw new TypeError( 'invalid argument. First argument must be a string. Value: `' + str + '`.' );
	}
	len = str.length;
	if ( arguments.length > 1 ) {
		if ( !isInteger( fromIndex ) ) {
			throw new TypeError( 'invalid argument. Second argument must be an integer. Value: `' + fromIndex + '`.' );
		}
		idx = fromIndex;
	} else {
		idx = len - 1;
	}
	if ( idx < 0 ) {
		idx += len;
	}
	if ( len === 0 || idx >= len ) {
		return -1;
	}

	prevCP = codePointAt( str, 0 );
	prev = getGraphemeBreakProperty( prevCP );
	prevEmoji = getEmojiProperty( prevCP );
	mid = [];
	midEmoji = [];
	ans = -1;
	for ( i = 1; i <= idx; i++ ) {
		if ( hasUTF16SurrogatePairAt( str, i-1 ) ) {
			ans = i-2;
			prevCP = codePointAt( str, i-1 );
			prev = getGraphemeBreakProperty( prevCP );
			prevEmoji = getEmojiProperty( prevCP );
			mid = [];
			midEmoji = [];
			continue;
		}
		nextCP = codePointAt( str, i );
		next = getGraphemeBreakProperty( nextCP );
		nextEmoji = getEmojiProperty( nextCP );
		if ( shouldBreak( prev, mid, next, prevEmoji, midEmoji, nextEmoji ) ) {
			ans = i-1;
			prevCP = codePointAt( str, i );
			prev = getGraphemeBreakProperty( prevCP );
			prevEmoji = getEmojiProperty( prevCP );
			mid = [];
			midEmoji = [];
			continue;
		}
		mid.push( next );
		midEmoji.push( nextEmoji );
	}
	return ans;
}


// EXPORTS //

module.exports = prevGraphemeClusterBreak;
